/**
 * Reorder parameters using given array
 * @example
 * reorderCall([1, 2, 0], fn)(a, b, c)
 * // is equivalent to: fn(b, c, a)
 */
function reorderParams(order, fn)
{
  return function () {
    var args = Array.prototype.slice.call(arguments);
    for (var i = 0; i < order.length; i++)
      args[i] = arguments[order[i]];
    fn.apply(this, args);
  };
}

module.exports = {
  reorderParams: reorderParams
};