function greater(a, b)
{
  return (a > b);
}

function greaterOrEq(a, b)
{
  return (a >= b);
}

function lesser(a, b)
{
  return (a < b);
}

function lesserOrEq(a, b)
{
  return (a <= b);
}

module.exports = {
  greater: greater,
  greaterOrEq: greaterOrEq,
  lesser: lesser,
  lesserOrEq: lesserOrEq
};
