#!/usr/bin/env node

var curry = require('functional/src/curry.js').curry;

function logThree(a, b, c) {
	return `a:${a}, b:${b}, c:${c}.`;
}

var a = 'A',
	b = { b: 'B' },
	c = 6;

var expected = logThree(a, b, c);
var failed = 0;
try {
  var tests = [
    { val: curry(logThree)(a)(b)(c), expected: true },
    { val: curry(logThree, a)(b)(c), expected: true },
    { val: curry(logThree, a, b)(c), expected: true },
    { val: curry(logThree, a, b, c), expected: false },
    { val: curry(logThree, a, b, c, 'd'), expected: false },
    { val: curry(logThree, a, b, c)('d'), expected: true },
  ];
} catch (e) {
  failed++;
  console.error(e);
}

console.log('expected: \n\t', expected); //DEBUG
console.log('tests: \n', tests); //DEBUG
tests && tests.forEach(function(test) {
    failed += (test.val === expected) !== test.expected;
});
if (!failed) {
	console.log('Success.');
} else {
	console.log(`Failed: ${failed} of ${tests && tests.length} failed.`);
}
